write_files = function(path, dest) {
	ifttt = googledrive::drive_ls(path, recursive = T) %>%
		dplyr::select(-drive_resource)
	print(glue::glue("{ifttt}, {dest}"))
	readr::write_tsv(ifttt, dest)
}
